import { fade, makeStyles, Theme } from "@material-ui/core";

const useStyles = makeStyles((theme: Theme) => ({
  // Global
  root: {
    height: "fit-content",
    minWidth: "6.25rem",
    minHeight: "2.75rem",
    padding: theme.spacing(1.5, 2),
    textTransform: "none",
    background: theme.palette.primary.main,
    fontSize: "1rem",
    color: theme.palette.text.secondary,
    "&:hover": {
      background: theme.palette.primary.main,
      boxShadow: `0px 4px 5px -2px ${fade(
        theme.palette.primary.main,
        0.2
      )},0px 7px 10px 1px ${fade(
        theme.palette.primary.main,
        0.14
      )},0px 2px 16px 1px ${fade(theme.palette.primary.main, 0.12)}`,
    },
    "&:disabled": {
      background: theme.palette.disabledBackground,
      color: theme.palette.text.disabled,
    },
  },

  // Button Filled
  error: {
    background: theme.palette.error.main,
    "&:hover": {
      background: theme.palette.error.main,
      boxShadow: `0px 4px 5px -2px ${fade(
        theme.palette.error.main,
        0.2
      )},0px 7px 10px 1px ${fade(
        theme.palette.error.main,
        0.14
      )},0px 2px 16px 1px ${fade(theme.palette.error.main, 0.12)}`,
    },
  },
  success: {
    background: theme.palette.success.main,
    "&:hover": {
      background: theme.palette.success.main,
      boxShadow: `0px 4px 5px -2px ${fade(
        theme.palette.success.main,
        0.2
      )},0px 7px 10px 1px ${fade(
        theme.palette.success.main,
        0.14
      )},0px 2px 16px 1px ${fade(theme.palette.success.main, 0.12)}`,
    },
  },

  // Sizes
  large: {
    padding: theme.spacing(2, 4),
  },
  medium: {
    padding: theme.spacing(1.5, 2.5),
  },
  small: {
    padding: theme.spacing(1, 1.25),
  },
}));

export { useStyles };
import { Button } from "@material-ui/core";
import React from "react";
import { ButtonBaseProps } from "../base";
import { useStyles } from "./styles";

type Variant = "default" | "error" | "success" | undefined;

type Size = "large" | "medium" | "small" | undefined;

interface ButtonFilledProps extends ButtonBaseProps {
  variant?: Variant;
  size?: Size;
}

const ButtonFilled: React.FC<ButtonFilledProps> = ({
  variant,
  size,
  children,
  className,
  ...rest
}) => {
  // Styles
  const classes = useStyles();

  function getVariant(variant: Variant): string {
    switch (variant) {
      case "error":
        return classes.error;
      case "success":
        return classes.success;
      default:
        return "";
    }
  }

  function getVariantSize(size: Size): string {
    switch (size) {
      case "large":
        return classes.large;
      case "medium":
        return classes.medium;
      case "small":
        return classes.small;
      default:
        return "";
    }
  }

  return (
    <Button
      variant="contained"
      className={`${classes.root} ${className} ${getVariant(
        variant
      )} ${getVariantSize(size)}`}
      {...rest}
    >
      {children}
    </Button>
  );
};

export { ButtonFilled };
