import React, { useState } from "react";
import SuccessIcon from "../../../assets/checkIcon.svg";
import { BaseInputProps } from "./base";
import { useStyles } from "./styles";

type Variant = "primary" | "error" | "success" | undefined;

interface InputProps extends BaseInputProps {
  variant?: Variant;
  width?: string;
  filled?: boolean;
  startIcon?: React.ReactNode;
  endIcon?: React.ReactNode;
}

const InputField: React.FC<InputProps> = ({
  variant,
  width,
  filled,
  startIcon,
  endIcon,
  disabled,
  type,
  fullWidth,
  className,
  ...rest
}) => {
  const classes = useStyles({ fullWidth, width: width ?? "25rem" });
  // Hides or shows the password
  const [showPassword, setShowPassword] = useState<boolean>(false);

  const handleClickShowPassword = () => {
    setShowPassword(!showPassword);
  };

  function getVariant(variant: Variant): string {
    switch (variant) {
      case "error":
        return classes.error;
      case "success":
        return classes.success;
      default:
        return classes.primary;
    }
  }

  return (
