  ...rest
}) => {
  const classes = useStyles({ color });

  function getVariant(variant: Variant): string {
    switch (variant) {
      case "bold":
        return classes.bold;
      case "small":
        return classes.small;
      default:
        return classes.primary;
    }
  }

  return (
    <Typography className={`${classes.root} ${getVariant(variant)}`} {...rest}>
      {children}
    </Typography>
  );
};

